import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './style.css';

const BlogList = ({ blogs }) => {
  const truncateDescription = (description, maxLines) => {
    const lines = description.split('\n');
    const truncated = lines.slice(0, maxLines).join('\n');
    return truncated;
  };

  const fetchCoverUrl = async (blog) => {
    try {
      if (blog.cover) {
        // Fetch the specific blog entry to get the full cover image URL
        const response = await axios.get(
          `https://cdn.contentful.com/spaces/lecsor65z6h5/entries/${blog.id}?access_token=BfTZj7xc714fKGvPfg7qnA1ZhQh3up_qyWNjQn8Jj8M`
        );

        return response.data.fields.cover?.fields?.file?.url || '';
      }

      return '';
    } catch (error) {
      console.error('Error fetching blog details:', error);
      return '';
    }
  };

  const renderImage = async (blog) => {
    const coverUrl = await fetchCoverUrl(blog);

    return (
      <React.Fragment>
        <img className="blogList-img" src={coverUrl} alt={blog.title} />
      </React.Fragment>
    );
  };

  return (
    <div className="blogList-wrap">
      {blogs.map((blog) => (
        <div key={blog.id} className="blog-item">
          {renderImage(blog)}
          <h3>{blog.title}</h3>
          <p className="truncated-description">{truncateDescription(blog.description, 10)}</p>
          <p>{`Author: ${blog.authorName}`}</p>
          <p>{`Category: ${blog.category}`}</p>
          <p>{`Created At: ${blog.createdAt}`}</p>
          <Link to={`/blog/${blog.id}`} className="read-more-link">
            Read More
          </Link>
        </div>
      ))}
    </div>
  );
};

export default BlogList;
