import React, { useEffect, useState } from 'react'
import Header from '../../components/Home/Header'
import Searchbar from '../../components/Home/Searchbar'
import BlogList from '../../components/Home/BlogList'
import { blogList } from '../../config/data'
import EmptyList from '../../components/common/EmptyList'
import axios from 'axios';


const Home = () => {

const [blogs,setBlogs]= useState([]);
const [searchkey,setSearchKey]=useState('');

useEffect(() => {
  // Fetch data from Contentful API
  const fetchData = async () => {
    try {
      const response = await axios.get(
        'https://api.contentful.com/your-contentful-api-endpoint'
        // Replace 'your-contentful-api-endpoint' with your actual Contentful API endpoint
      );

      // Assuming your data is in response.data.items, adjust this based on your Contentful structure
      const fetchedBlogs = response.data.items;
      setBlogs(fetchedBlogs);
    } catch (error) {
      console.error('Error fetching data from Contentful:', error);
    }
  };

  fetchData();
}, []); // Empty dependency array means this effect runs once when the component mounts


const handleSearchSubmit=event=>{
  event.preventDefault();
  handleSearchResults();
}

const handleSearchBar = (e) => {
  e.preventDefault();
  handleSearchResults();
};

const handleSearchResults=()=>{
  // const allBlogs=blogList;
  // const filteredBlogs=allBlogs.filter((blog)=>
  // blog.category.toLowerCase().includes(searchkey.toLowerCase().trim()));
  // setBlogs(filteredBlogs);
  const filteredBlogs = blogs.filter((blog) =>
      blog.category.toLowerCase().includes(searchKey.toLowerCase().trim())
    );
    setBlogs(filteredBlogs);
}

const handleClearSearch=()=>{
  // setBlogs(blogList);
  // setSearchKey('');
  fetchData();
    setSearchKey('');
}

  return (
    <div>

          <Header/>
          <Searchbar value={searchkey} clearSearch={handleClearSearch} formSubmit={handleSearchBar} handleSearchKey={(e)=>
          setSearchKey(e.target.value)}/>
          {!blogs.length? <EmptyList/> :<BlogList blogs={blogs}/>}

    </div>
  );
};

export default Home;
