import React, { useState, useEffect } from 'react';
import Header from '../../components/Home/Header';
import Searchbar from '../../components/Home/Searchbar';
import BlogList from '../../components/Home/BlogList';
import EmptyList from '../../components/common/EmptyList';
import { useQuery, gql, ApolloClient, InMemoryCache } from '@apollo/client';

// Replace with your Contentful space ID and access token
const SPACE_ID = 'lecsor65z6h5';
const ACCESS_TOKEN = 'BfTZj7xc714fKGvPfg7qnA1ZhQh3up_qyWNjQn8Jj8M';

const client = new ApolloClient({
  uri: `https://graphql.contentful.com/content/v1/spaces/${SPACE_ID}`,
  cache: new InMemoryCache(),
  headers: {
    Authorization: `Bearer ${ACCESS_TOKEN}`,
  },
});

const GET_BLOGS = gql`
  query GetBlogs {
    blogPostCollection {
      items {
        sys {
          id
        }
        title
        category
        subCategory
        description
        authorName
        authorAvatar
        createdAt
        cover {
          url
        }
      }
    }
  }
`;

const Home = () => {
  const [blogs, setBlogs] = useState([]);
  const [searchKey, setSearchKey] = useState('');

  const { loading, error, data } = useQuery(GET_BLOGS, { client });

  useEffect(() => {
    if (data && data.blogPostCollection && data.blogPostCollection.items) {
      setBlogs(data.blogPostCollection.items);
    }
  }, [data]);

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    handleSearchResults();
  };

  const handleSearchResults = () => {
    const filteredBlogs = blogs.filter((blog) =>
      blog.category.toLowerCase().includes(searchKey.toLowerCase().trim())
    );
    setBlogs(filteredBlogs);
  };

  const handleClearSearch = () => {
    if (data && data.blogPostCollection && data.blogPostCollection.items) {
      setBlogs(data.blogPostCollection.items);
    }
    setSearchKey('');
  };

  return (
    <div>
      <Header />
      <Searchbar
        value={searchKey}
        clearSearch={handleClearSearch}
        formSubmit={handleSearchSubmit}
        handleSearchKey={(e) => setSearchKey(e.target.value)}
      />
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error fetching data from Contentful: {error.message}</p>
      ) : !blogs.length ? (
        <EmptyList />
      ) : (
        <BlogList blogs={blogs} />
      )}
    </div>
  );
};

export default Home;
