// src/pages/Home/index.jsx

import React, { useState, useEffect } from 'react';
import Header from '../../components/Home/Header';
import Searchbar from '../../components/Home/Searchbar';
import BlogList from '../../components/Home/BlogList';
import EmptyList from '../../components/common/EmptyList';
import axios from 'axios';

const Home = () => {
  const [blogs, setBlogs] = useState([]);
  const [searchKey, setSearchKey] = useState('');

  const spaceId = lecsor65z6h5; // Replace with your Contentful Space ID
  const accessToken = BfTZj7xc714fKGvPfg7qnA1ZhQh3up_qyWNjQn8Jj8M; // Replace with your Contentful Access Token

  const fetchData = async () => {
    try {
      const response = await axios.get(
        `https://cdn.contentful.com/spaces/${spaceId}/entries?access_token=${accessToken}&content_type=blogPost`
      );

      const fetchedBlogs = response.data.items.map((item) => ({
        id: item.sys.id,
        title: item.fields.title,
        category: item.fields.category,
        subCategory: item.fields.subCategory,
        description: item.fields.description,
        authorName: item.fields.authorName,
        authorAvatar: item.fields.authorAvatar,
        createdAt: item.fields.createdAt,
        cover: item.fields.cover.fields.file.url,
      }));

      setBlogs(fetchedBlogs);
    } catch (error) {
      console.error('Error fetching data from Contentful:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []); // Empty dependency array means this effect runs once when the component mounts

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    // Implement your search logic based on the fetched blogs
    // You can use the 'blogs' state for filtering
    const filteredBlogs = blogs.filter((blog) =>
      blog.category.toLowerCase().includes(searchKey.toLowerCase().trim())
    );
    setBlogs(filteredBlogs);
  };

  const handleClearSearch = () => {
    // Reset to the original fetched blogs
    fetchData();
    setSearchKey('');
  };

  return (
    <div>
      <Header />
      <Searchbar
        value={searchKey}
        clearSearch={handleClearSearch}
        formSubmit={handleSearchSubmit}
        handleSearchKey={(e) => setSearchKey(e.target.value)}
      />
      {!blogs.length ? <EmptyList /> : <BlogList blogs={blogs} />}
    </div>
  );
};

export default Home;
