import React from 'react';
import Header from '../../components/Home/Header';
import Searchbar from '../../components/Home/Searchbar';
import BlogList from '../../components/Home/BlogList';
import EmptyList from '../../components/common/EmptyList';
import { blogList } from '../../config/data';

const Home = () => {
  // ... (existing code)

  const handleLinkedInShare = () => {
    // Replace the URL and text with the appropriate values for your site
    const shareUrl = 'https://your-website-url.com';
    const title = 'Check out these amazing blog posts!';
    window.open(`https://www.linkedin.com/sharing/share-offsite/?url=${shareUrl}&title=${title}`);
  };

  const handleFacebookShare = () => {
    // Replace the URL and text with the appropriate values for your site
    const shareUrl = 'https://your-website-url.com';
    const quote = 'Check out these amazing blog posts!';
    window.open(`https://www.facebook.com/sharer/sharer.php?u=${shareUrl}&quote=${quote}`);
  };

  return (
    <div>
      <Header />
      <Searchbar
        value={searchKey}
        clearSearch={handleClearSearch}
        formSubmit={handleSearchSubmit}
        handleSearchKey={(e) => setSearchKey(e.target.value)}
      />
      <div>
        <button onClick={handleLinkedInShare}>Share on LinkedIn</button>
        <button onClick={handleFacebookShare}>Share on Facebook</button>
      </div>
      {!blogs.length ? <EmptyList /> : <BlogList blogs={blogs} />}
    </div>
  );
};

export default Home;
