// src/pages/Home/index.jsx

import React, { useState } from 'react';
import Header from '../../components/Home/Header';
import Searchbar from '../../components/Home/Searchbar';
import BlogList from '../../components/Home/BlogList';
import EmptyList from '../../components/common/EmptyList';
import { useQuery, gql } from '@apollo/client';

const GET_BLOGS = gql`
  query {
    blogs {
      id
      title
      category
      subCategory
      description
      authorName
      authorAvatar
      createdAt
      cover
    }
  }
`;

const Home = () => {
  const [searchKey, setSearchKey] = useState('');
  const { loading, error, data } = useQuery(GET_BLOGS);

  // Your data fetching and rendering logic goes here
  const blogs = data ? data.blogs : [];

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    // Implement your search logic based on the fetched blogs
    // You can use the 'blogs' state for filtering
    const filteredBlogs = blogs.filter((blog) =>
      blog.category.toLowerCase().includes(searchKey.toLowerCase().trim())
    );
    // Update the state or perform any other necessary action with the filteredBlogs
  };

  const handleClearSearch = () => {
    // You may refetch the data here or update the UI as needed
    setSearchKey('');
  };

  return (
    <div>
      <Header />
      <Searchbar
        value={searchKey}
        clearSearch={handleClearSearch}
        formSubmit={handleSearchSubmit}
        handleSearchKey={(e) => setSearchKey(e.target.value)}
      />
      {!blogs.length ? <EmptyList /> : <BlogList blogs={blogs} />}
    </div>
  );
};

export default Home;
