import React from 'react';
import { Link } from 'react-router-dom';
import { documentToReactComponents } from '@contentful/rich-text-react-renderer';
import './style.css';

const BlogList = ({ blogs }) => {
  return (
    <div className="blogList-wrap">
      {blogs.map((blog) => (
        <div key={blog.id} className="blog-item">
          {/* Check if cover exists before rendering the image */}
          {blog.cover && (
            <img src={blog.cover.file.url} alt={blog.title} />
          )}

          <h3>{blog.title}</h3>

          {/* Render rich text content including images */}
          {blog.description && documentToReactComponents(blog.description, renderOptions)}

          <p>{`Author: ${blog.authorName}`}</p>
          <p>{`Category: ${blog.category}`}</p>
          <p>{`Created At: ${blog.createdAt}`}</p>

          <Link to={`/blog/${blog.id}`} className="read-more-link">
            Read More
          </Link>
        </div>
      ))}
    </div>
  );
};

// Define your render options object
const renderOptions = {
  renderNode: {
    'embedded-asset-block': (node) => {
      const { title, description, file } = node.data.target.fields;
      const imageUrl = file.url;
      return (
        <img
          src={imageUrl}
          alt={description || title || 'Image'}
          className="my-4 max-w-full"
        />
      );
    },
  },
};

export default BlogList;
